CREATE TABLE `receipts` (  `receipt_id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `ps_id` int(10) unsigned zerofill NOT NULL DEFAULT 0 COMMENT '仓库id',  `company_id` varchar(30) NOT NULL COMMENT '仓库id',  `receipt_no` int(10) unsigned NOT NULL COMMENT '入库单id',  `status` varchar(10) DEFAULT NULL COMMENT 'open,closed,imported,reveived',  `customer_key` int(10) unsigned zerofill DEFAULT NULL COMMENT '客户id',  `customer_id` varchar(10) DEFAULT NULL COMMENT 'WMS客户id',  `supplier_key` int(10) unsigned DEFAULT NULL COMMENT '供应商id',  `supplier_id` varchar(10) DEFAULT NULL COMMENT 'WMS供应商(title)',  `entered_date` datetime DEFAULT NULL COMMENT '建单日期',  `warehouse_id` varchar(15) DEFAULT NULL COMMENT '仓库id',  `reference_no` varchar(50) DEFAULT NULL COMMENT '参考号',  `po_no` varchar(50) DEFAULT NULL COMMENT '采购单id',  `bol_no` varchar(50) DEFAULT NULL COMMENT '出库单号',  `container_no` varchar(50) DEFAULT NULL COMMENT '货柜号',  `seals` varchar(50) DEFAULT NULL COMMENT '封条',  `scac` varchar(10) DEFAULT NULL COMMENT '卡车公司编码',  `carrier_id` varchar(15) DEFAULT NULL COMMENT 'WMS运输公司',  `schedule_date` datetime DEFAULT NULL COMMENT '预定时间',  `appointment_date` datetime DEFAULT NULL COMMENT '预约时间',  `in_yard_date` datetime DEFAULT NULL COMMENT '抵达时间',  `devanner_date` datetime DEFAULT NULL COMMENT '入库时间',  `last_free_date` datetime DEFAULT NULL COMMENT '码头最后免费存货日期',  `receipt_type` varchar(15) DEFAULT NULL COMMENT 'transfer,receipt',  `note` varchar(1000) DEFAULT NULL COMMENT '备注',  `carton_label_printed` bit(1) DEFAULT NULL COMMENT '箱于标签是否已打印',  `plate_printed` bit(1) DEFAULT NULL COMMENT '托盘标签是否已打印',  `receipt_ticket_printed` bit(1) DEFAULT NULL COMMENT '收货单票据是否已打印',  `tally_sheet_printed` bit(1) DEFAULT NULL COMMENT '记录单是否已打印',  `receipt_printed` bit(1) DEFAULT NULL COMMENT '收货单是否已打印',  `link_sequence_no` varchar(10) DEFAULT NULL,  `re_control_no` varchar(10) DEFAULT NULL,  `edi_order` bit(1) DEFAULT NULL,  `send944` bit(1) DEFAULT NULL,  `send947` bit(1) DEFAULT NULL,  `date_created` datetime DEFAULT NULL COMMENT '创建时间',  `user_created` varchar(45) DEFAULT NULL COMMENT '创建人',  `date_updated` datetime DEFAULT NULL COMMENT '最后更新时间',  `user_updated` varchar(45) DEFAULT NULL COMMENT '最后更新人',  `detail_id` int(10) unsigned DEFAULT NULL COMMENT 'door_or_location_occupancy_details',  `print_date` datetime DEFAULT NULL COMMENT '最近打印时间',  `print_user` int(10) unsigned DEFAULT NULL COMMENT '最近打印人',  `print_number` int(10) DEFAULT NULL COMMENT '最近打印次数',  `close_user` int(10) unsigned DEFAULT NULL COMMENT 'close user adid',  PRIMARY KEY (`receipt_id`)) ENGINE=InnoDB AUTO_INCREMENT=485 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;CREATE TABLE `receipt_lines` (  `receipt_line_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',  `receipt_id` int(10) unsigned DEFAULT NULL COMMENT '主单据ID',  `company_id` varchar(15) DEFAULT NULL COMMENT '仓库id',  `receipt_no` int(10) unsigned DEFAULT NULL COMMENT '入库单id',  `line_no` int(10) DEFAULT NULL COMMENT '序号',  `item_id` varchar(30) DEFAULT NULL COMMENT '商品id',  `warehouse_id` varchar(15) DEFAULT NULL COMMENT '仓库id',  `zone_id` varchar(15) DEFAULT NULL COMMENT '区域id',  `location_id` varchar(15) DEFAULT NULL COMMENT '位置id',  `pallet_size` varchar(15) DEFAULT NULL COMMENT '托盘尺寸id',  `po_no` varchar(50) DEFAULT NULL COMMENT '采购单id',  `po_line_no` int(10) DEFAULT NULL COMMENT '采购单序号',  `lot_no` varchar(40) DEFAULT NULL COMMENT '批次',  `goods_total` int(10) DEFAULT NULL COMMENT 'line下所有收到的正常的货物',  `damage_total` int(10) DEFAULT NULL COMMENT 'line下所有收到的损坏的货物',  `expected_qty` int(10) DEFAULT NULL COMMENT '预定数量',  `pallets` double(10,2) DEFAULT NULL COMMENT '托盘数',  `note` varchar(1000) DEFAULT NULL COMMENT '备注',  `qty_per_pallet` int(10) DEFAULT NULL COMMENT '推荐每托盘装货数,对应QtyPerUnit',  `is_check_sn` bit(1) DEFAULT NULL COMMENT '是否需要sn的验证,对应ValidatedInboundSerialNo',  `sn_size` int(10) DEFAULT NULL COMMENT 'sn长度,对应SerialNoLength',  `is_check_lot_no` bit(1) DEFAULT NULL COMMENT '是否需要lot_no验证,对应SerialNoScanLotNoCheck',  `wms_item` varchar(30) DEFAULT NULL,  `wms_lot_no` varchar(40) DEFAULT NULL,  `wms_qty` int(10) DEFAULT NULL,  `supplier` varchar(10) DEFAULT NULL COMMENT '供应商(title)',  `status` int(10) DEFAULT '0' COMMENT '0:WMS原始数据;2:做完count;3:做完CC;4:做完scan',  `is_sku_type` bit(1) DEFAULT NULL COMMENT '0:表示普通line;1:表示sku类型的cc task',  `pc_id` int(10) unsigned DEFAULT NULL COMMENT '商品pcid',  `close_date` datetime DEFAULT NULL COMMENT '关闭时间',  `close_user` int(10) unsigned DEFAULT NULL COMMENT '关闭人',  `detail_id` int(10) unsigned DEFAULT NULL COMMENT 'door_or_location_occupancy_details',  `unloading_start_time` datetime DEFAULT NULL,  `unloading_finish_time` datetime DEFAULT NULL,  `counted_user` int(10) unsigned DEFAULT NULL COMMENT '最后一个count的人',  `scan_start_adid` int(10) unsigned DEFAULT NULL,  `scan_start_time` datetime DEFAULT NULL,  `scan_end_adid` int(10) unsigned DEFAULT NULL,  `scan_end_time` datetime DEFAULT NULL,  `close_adid` int(10) unsigned DEFAULT NULL,  `close_time` datetime DEFAULT NULL,  `assign_time` datetime DEFAULT NULL,  `cc_start_time` datetime DEFAULT NULL,  `cc_finish_time` datetime DEFAULT NULL,  `cc_user` int(10) unsigned DEFAULT NULL COMMENT '做cc的人',  PRIMARY KEY (`receipt_line_id`),  KEY `fk_receipt_id` (`receipt_id`),  CONSTRAINT `fk_receipt_id` FOREIGN KEY (`receipt_id`) REFERENCES `receipts` (`receipt_id`)) ENGINE=InnoDB AUTO_INCREMENT=645 DEFAULT CHARSET=utf8;